import { Component, OnInit, Input } from '@angular/core';
import { TableService } from '../../services/table.service';
import { FakeData } from '../../classes/fake-data';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.sass']
})
export class TableComponent implements OnInit {

    private data: FakeData[];
    @Input() params: any;
    title = 'Tytuł tabeli';

    constructor(
        public ts: TableService) { }

    ngOnInit() {
        this.ts.getData().then(body => {
            this.data = body;
            console.log(this.data);
        });
        console.log(this.params);
    }

    manageRowClass(d: number) {

        let klass: string;

        switch (d) {
            case 4:
                klass = 'table-success';
                break;
            case 6:
                klass = 'table-danger';
                break;
        }
        return klass || '';
    }

    manageTableClass() {

        let klass = [];

        if (this.params.small) {
            klass.push('table-sm');
        }
        if (this.params.striped) {
            klass.push('table-striped');
        }
        if (this.params.bordered) {
            klass.push('table-bordered');
        }
        if (this.params.hover) {
            klass.push('table-hover');
        }
        return klass.length ? klass.join(' ') : '';
    }
}
