export class Alert {
    body: string;       // alert body
    mode: string;       // alert type [success, info, warning, danger]
    time: number;       // alert visiblity time [miliseconds]
}

import { Injectable } from '@angular/core';
import { Alert } from '../classes/alert';

@Injectable()
export class AlertService {

    alert: Alert = new Alert();

    constructor() { }

    makeAlert(alert: Alert) {
        /*
         * show alert
         */
        if (!this.alert.body) {

            this.alert = alert;

            setTimeout(() => {
                this.alert.body = undefined;
            }, this.alert.time || 3000);
        }
    }
}
