import { Component, OnInit, OnChanges, ViewChild, ElementRef, Renderer2, Input } from '@angular/core';

@Component({
    selector: 'app-logger',
    templateUrl: './logger.component.html',
    styleUrls: ['./logger.component.scss']
})
export class LoggerComponent implements OnInit, OnChanges {

    @ViewChild('logger') logger: ElementRef;
    @Input('clickedButtonInput') clickedButton: string;
    log: string[] = [];

    constructor(
        private renderer: Renderer2
    ) { }

    ngOnInit() {}

    ngOnChanges() {

        if (this.clickedButton) {

            this.log.push(this.clickedButton);

            this.renderer.setProperty(
                this.logger.nativeElement,
                'value',
                this.log.join(',\n')
            );
        }
    }

}
