@NgModule({
  declarations: [
    // Lista komponentów, dyrektyw i pipes należących do tego modułu
  ],
  imports: [
    // Lista modułów, które mają zostać zaimportowane do tego modułu. Wszystkie moduły
    // z tej listy są dostępne dla rzeczy zadeklarowanych w sekcji "declarations"
  ],
  exports: [
    // Lista komponentów, dyrektyw i pipes, które mają być widoczne dla modułów
    // importujących bieżący moduł
  ],
  providers: [
    // Lista dostawców wstrzykniętych za pomocą dependency injection, widocznych
    // dla bieżącego modułu jak i dla modułów go importujących
  ],
  bootstrap: [
    // Lista komponentów do załadowania wraz z bieżącym komponentem
  ]
})
