import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivityService } from "../../services/activity.service";
import { Observable } from 'rxjs/Observable';
import { Subscription } from 'rxjs/Subscription';
import { Helpers } from '../../classes/helpers'; // NEW

@Component({
    selector: 'app-jokes',
    templateUrl: './jokes.component.html',
    styleUrls: ['./jokes.component.scss']
})
export class JokesComponent implements OnInit, OnDestroy {

    output: any;
    subscription: Subscription;
    helpers: Helpers = new Helpers(); // NEW

    private activities$: Observable<any>;

    constructor(
        private activityService: ActivityService
    ) { }

    ngOnInit() {
        this.subscription = this.activityService
                                    .getActivities()
                                        .subscribe(
                                            data => this.output = data.value.joke
                                        );
    }

    ngOnDestroy() {
        // unsubscribe to ensure no memory leaks
        this.subscription.unsubscribe();
    }

}
